mapper.select:
select m.mno, m.id, m.path, m.nick, m.intro, m.pwd, m.type
from memb m
where m.mno = #{memberMno}

mapper.update:
update memb set 
  nick=#{nickname}, 
  pwd=password(#{password}),
  intro=#{selfIntro},
  path=#{path}
where mno=#{mno}

update memb set 
  nick=#{nickname}, 
  intro=#{selfIntro},
  path=#{path}
where mno=#{mno}
